SELECT s002b.CMNS_CD_GRP AS "cmnsGrpCd" , s001b.CMNS_CD_GRP_EXPL AS "cmnsGrpCdNm" , s001b.CMNS_CD_NM AS "cmnsGrpCdNmEng" , s001b.CD_LNGTH AS "cdLength" , s002b.CD_VL_ID AS "cdValue" , s002b.CD_VL_NM AS "cdName" FROM IBIMS002B s002b LEFT JOIN IBIMS001B s001b ON s001b.CMNS_CD_GRP = s002b.CMNS_CD_GRP WHERE 1=1 AND s002b.USE_YN = 'Y' AND s002b.DEL_YN = 'N' AND s001b.USE_YN = 'Y' AND s001b.DEL_YN = 'N'
AND s001b.CMNS_CD_GRP = #{cmnsCdGrp}
ORDER BY s001b.CMNS_CD_GRP, s002b.CD_VL_ID

