mapper.insert:
        INSERT INTO IBIMS421B 
            (
                FEE_KND_CD
              , ACTS_CD
              , RGST_STTS_CD
              , FEE_NAME
              , FEE_RCOG_DCD
              , FEE_RCKN_DCD
              , FEE_RT
              , FEE_LWST_AMT
              , FEE_HGST_AMT
              , ACT_NAME
              , ACCT_JOB_CD
              , ACCT_UN_JOB_CD
              , ACCT_TR_CD
              , HND_DETL_DTM
              , HND_EMPNO
              , HND_TMNL_NO
              , HND_TR_ID
              , GUID
            )
        VALUES
            (
                #{feeKndCd}
              , #{actsCd}
              , '1'
              , #{feeName}
              , #{feeRcogDcd}
              , #{feeRcknDcd}
              , #{feeRt}
              , #{feeLwstAmt}
              , #{feeHgstAmt}
              , (SELECT L.CD_VL_NM
					FROM IBIMS002B L
					WHERE 1=1
					AND L.CMNS_CD_GRP =  'A005'
					AND L.CD_VL_ID = #{actsCd} 
				)
              , #{acctJobCd}
              , #{acctUnJobCd}
              , #{acctTrCd}
              , SYSDATE
              , #{hndEmpno}
              , #{hndTmnlNo}
              , #{hndTrId}
              , #{guid}
            )

mapper.update:
UPDATE IBIMS421B
   SET FEE_NAME = #{feeName}
     , FEE_RCOG_DCD = #{feeRcogDcd}
     , FEE_RCKN_DCD = #{feeRcknDcd}
     , FEE_RT = #{feeRt}
     , FEE_LWST_AMT = #{feeLwstAmt}
     , FEE_HGST_AMT = #{feeHgstAmt}
     , ACT_NAME = #{actName}
     , ACCT_JOB_CD = #{acctJobCd}
     , ACCT_UN_JOB_CD = #{acctUnJobCd}
     , ACCT_TR_CD = #{acctTrCd}
     , HND_DETL_DTM = SYSDATE
     , HND_EMPNO = #{hndEmpno}
     , HND_TMNL_NO = #{hndTmnlNo}
     , HND_TR_ID = #{hndTrId}
     , GUID = #{guid}
 WHERE 1=1
   AND FEE_KND_CD = #{feeKndCd}
   AND ACTS_CD = #{actsCd}

    	MERGE INTO IBIMS421B T
    	USING DUAL
    	ON ( FEE_KND_CD =  #{feeKndCd} )
    	WHEN MATCHED THEN
    	UPDATE SET
    		   ACTS_CD = #{actsCd}
    		 , ACT_NAME= (SELECT L.CD_VL_NM
							FROM IBIMS002B L
							WHERE 1=1
							AND L.CMNS_CD_GRP =  'A005'
							AND L.CD_VL_ID = #{actsCd} 
						  )
    		 , FEE_NAME = #{feeName}
             , FEE_RCOG_DCD = #{feeRcogDcd}
             , FEE_RCKN_DCD = #{feeRcknDcd}
             , FEE_RT = #{feeRt}
             , FEE_LWST_AMT = #{feeLwstAmt}
             , FEE_HGST_AMT = #{feeHgstAmt}
             , ACCT_JOB_CD = #{acctJobCd}
             , ACCT_UN_JOB_CD = #{acctUnJobCd}
             , ACCT_TR_CD = #{acctTrCd}
             , HND_DETL_DTM = SYSDATE
             , HND_EMPNO = #{hndEmpno}
             , HND_TMNL_NO = #{hndTmnlNo}
             , HND_TR_ID = #{hndTrId}
             , GUID = #{guid}
		WHEN NOT MATCHED THEN
		INSERT 
    		 (
                FEE_KND_CD
              , ACTS_CD
              , RGST_STTS_CD
              , FEE_NAME
              , FEE_RCOG_DCD
              , FEE_RCKN_DCD
              , FEE_RT
              , FEE_LWST_AMT
              , FEE_HGST_AMT
              , ACT_NAME
              , ACCT_JOB_CD
              , ACCT_UN_JOB_CD
              , ACCT_TR_CD
              , HND_DETL_DTM
              , HND_EMPNO
              , HND_TMNL_NO
              , HND_TR_ID
              , GUID
            )
        VALUES
            (
                LPAD(  NVL(
    						(SELECT MAX(FEE_KND_CD)
                             FROM IBIMS421B a 	) , 0
                           ) +1
                         , 2, '0'
                    )
              , #{actsCd}
              , '1'
              , #{feeName}
              , #{feeRcogDcd}
              , #{feeRcknDcd}
              , #{feeRt}
              , #{feeLwstAmt}
              , #{feeHgstAmt}
              , (SELECT L.CD_VL_NM
					FROM IBIMS002B L
					WHERE 1=1
					AND L.CMNS_CD_GRP =  'A005'
					AND L.CD_VL_ID = #{actsCd} 
				)
              , #{acctJobCd}
              , #{acctUnJobCd}
              , #{acctTrCd}
              , SYSDATE
              , #{hndEmpno}
              , #{hndTmnlNo}
              , #{hndTrId}
              , #{guid}
            )

mapper.delete:
DELETE FROM IBIMS421B
 WHERE 1=1
   AND FEE_KND_CD = #{feeKndCd}
